package com.future.dubbo.controller;import com.future.dubbo.service.Hello;import org.apache.dubbo.config.annotation.DubboReference;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;/** * @Description: * @Author: lilei58 * @Date: Created in 2022/1/16 下午3:39 */@RestController@RequestMapping("/springboot")public class TestController {    @DubboReference    private Hello hello;    @GetMapping(value = "/test")    public String say(@RequestParam(name = "name") String name){        return hello.sayHello(name);    }}