package com.future.dubbo.service;import org.apache.dubbo.common.URL;import org.apache.dubbo.common.extension.Adaptive;import org.apache.dubbo.common.extension.SPI;/** * @Description: * @Author: lilei58 * @Date: Created in 2022/1/16 上午12:56 */@SPIpublic interface Hello {    void getHello();    /**     * 调用这个方法的时候找到对应的实现类     * 也就是 Map 中 key 为 hello 所对应的 value 值     * @param url     */    @Adaptive("hello")    void getHelloForUrl(URL url);}