package com.future.dubbo.service.impl;import com.future.dubbo.service.Hello;import org.apache.dubbo.common.URL;import org.apache.dubbo.config.annotation.DubboService;import org.springframework.stereotype.Component;/** * @Description: * @Author: lilei58 * @Date: Created in 2022/1/27 上午6:55 */@Component@DubboService(interfaceClass = Hello.class)public class ChinaHelloImpl implements Hello {    private Hello hello;    /**     * 注入点     *     * SPT注入的过程:     * 通过 SpiExtensionFactory 获取 Car的Adaptive 类,所以注入进来的对象其实是一个 Adaptive 类对象,代理对象...     *     * @param hello     */    public void setHello(Hello hello) {        this.hello = hello;    }    @Override    public void sayHello() {        System.out.println("ChinaHelloImpl");        hello.sayHello();    }    @Override    public void helloForUrl(URL url) {        System.out.println("ChinaHelloImpl");        hello.helloForUrl(url);    }}