package com.future.dubbo.service.impl;import com.future.dubbo.service.Hello;import org.apache.dubbo.common.URL;import org.apache.dubbo.config.annotation.DubboService;import org.apache.dubbo.rpc.RpcContext;import org.springframework.stereotype.Component;/** * @Description: * @Author: lilei58 * @Date: Created in 2022/1/16 上午12:58 */@Component@DubboService(interfaceClass = Hello.class)public class HelloImpl implements Hello {    public String sayHello(String name) {        URL url = RpcContext.getContext().getUrl();        return "Hello " + name + " " + String.format("protocol is %s, address is %s", url.getProtocol(), url.getAddress());    }    @Override    public void sayHello() {        System.out.println("HelloImpl");    }    @Override    public void helloForUrl(URL url) {        System.out.println("HelloImpl");    }}